mapper.select:

select 

from DMIN_GRID_LAYER_LEVEL
where GRIB_VERSION = #{gribVersion,jdbcType=INTEGER}
  and LEVEL_TYPE = #{levelType,jdbcType=INTEGER}

mapper.delete:

delete from DMIN_GRID_LAYER_LEVEL
where GRIB_VERSION = #{gribVersion,jdbcType=INTEGER}
  and LEVEL_TYPE = #{levelType,jdbcType=INTEGER}

mapper.update:

update DMIN_GRID_LAYER_LEVEL
set RECORD_ID = #{recordId,jdbcType=VARCHAR},
  LEVEL_CODE = #{levelCode,jdbcType=VARCHAR},
  SCALE_DIVISOR = #{scaleDivisor,jdbcType=NUMERIC},
  LEVEL_PROPERITY = #{levelProperity,jdbcType=VARCHAR},
  LEVEL_NAME = #{levelName,jdbcType=VARCHAR},
  UNIT = #{unit,jdbcType=VARCHAR}
where GRIB_VERSION = #{gribVersion,jdbcType=INTEGER}
  and LEVEL_TYPE = #{levelType,jdbcType=INTEGER}
